mapper.select:
select exists (
    select 1 from user where email = #{email}
)

select exists (
    select 1 from user where nickname = #{nickname}
)

mapper.update:
update user
set password=#{encodedPassword}
where user.user_id=#{userId}

update user
set nickname=#{nickname}, phone=#{phone}
where user.user_id=#{userId}
